import React, { useState } from 'react';
import { Text, TextInput, Button, View } from 'react-native';

const SignUp_PassWord = () => {

	const [password, setPassword] = useState("");
	const [ckPassword, checkPassword] = useState("");
	const [opacity, setOpacity] = useState(0);

	const checkFormPassword = (input) => {
		if (input.length > 7 && input.length < 21)
			return true;
		else
			return false;
  	};

	const checkSamePassword = (input) => {
		if (input == password)
			return true;
		else
			return false;
	}

	return (
		<>
			<TextInput placeholder="비밀번호" onChangeText={(input) => {
				if (checkFormPassword(input)) {
					setPassword(input);
					setOpacity(0);
				}
				else
					setOpacity(100);
			}}/>
			<Text style={{ opacity: opacity }}>잘못된 비밀번호 형식입니다.</Text>
			<TextInput placeholder="비밀번호 확인" />
			<Text style={{ opacity: 0 }}>비밀번호를 다시 확인해주세요.</Text>
		</>
	);
}

export default SignUp_PassWord;
