import React, { useState } from 'react';
import { TextInput } from 'react-native';
import { food_name } from './data';
import ShowSearchPreview from './ShowSearchPreview';

const ShowSearchBar = () => {

	const [previewFood, setPreviewFood] = useState([]);
	const matchFoodName = (input) => {
		let food = [];
		if (input.length > 0) {
			food = food_name.filter(food => food.includes(input));
			const foodObject = food.map(food => {
				let object = {};
				object.key = food;
				return object;
			});
		}
		setPreviewFood(foodObject);
	};
	console.log("previewFood: ");
	console.log(previewFood);
	return (
		<>
			<TextInput placeholder="좋아하는 음식 검색!" onChangeText={(input) => matchFoodName(input)} />
			<ShowSearchPreview previewFood={previewFood}/>
		</>
	);
}

export default ShowSearchBar;
